mapper.select:
/* get_member */
SELECT    IDX, ID, NAME, TEL, LEVEL
FROM      MEMBER
WHERE     IDX = #{idx}

mapper.update:
UPDATE MEMBER
SET    AGREE = 'Y'
WHERE  IDX = #{idx}

/* member_save */
UPDATE MEMBER
SET    NAME = #{name},
       TEL  = #{tel},

       PW = #{pw},

       LEVEL = #{level}
WHERE  IDX = #{idx}
