mapper.select:
/* get_jointBrokerage_list */ SELECT * FROM (SELECT @rownum := @rownum + 1 AS no, JB.IDX, (SELECT IMG_PATH FROM JOINT_IMG WHERE J_IDX = JB.IDX AND ORDER_NUM = 1) AS IMG_PATH, JB.TITLE, JB.SHARE_YN, JB.TYPE, 0 AS PRICE, NULL AS ROOM_TYPE, JB.ADDRESS1, JB.ADDRESS2, JB.STATUS, CASE WHEN STATUS = 'A' THEN '중개가능' WHEN STATUS = 'I' THEN '계약상태' WHEN STATUS = 'E' THEN '계약완료' END AS STATUS_TXT, DATE_FORMAT(JB.REGDATE, '%Y-%m-%d %H:%i:%s') AS REGDATE FROM JOINT_BROKERAGE JB, (SELECT @ROWNUM := 0) R WHERE 1=1
AND DATE_FORMAT(JB.REGDATE, '%Y-%m-%d') >= #{start_date}
AND DATE_FORMAT(JB.REGDATE, '%Y-%m-%d') <= #{end_date}
ORDER BY IDX DESC ) a WHERE no BETWEEN #{start} AND #{end}
