mapper.select:
/* get_real_cnt */
SELECT    COUNT(*)
FROM      REAL_ESTATE
WHERE     1=1
AND       MEMBER_IDX = #{member_idx}

/* get_real_list */ SELECT a.* FROM (SELECT @rownum := @rownum + 1 AS no, b.IDX, b.TEL, b.NAME, ADB.CITY AS PLACE_TYPE, b.EMAIL, b.PREFERENCE, CC.CODE_NAME AS TYPE, date_format(b.REGDATE, '%Y-%m-%d %H:%i:%s') as REGDATE, b.REGID FROM REAL_ESTATE b LEFT OUTER JOIN ADMINISTRATIVE_DISTRICT ADB ON b.PLACE_TYPE = ADB.idx LEFT OUTER JOIN ADMINISTRATIVE_DISTRICT ADP ON b.PREFERENCE = ADP.idx LEFT OUTER JOIN COMMON_CODE_D CC ON b.TYPE = CC.CODE AND CC.CODE_H = 'TYPE', (SELECT @ROWNUM := 0) R WHERE 1 = 1 AND b.MEMBER_IDX = #{member_idx}
AND b.TYPE = #{type}
AND b.city = #{city}
AND b.PLACE_TYPE = #{district}
AND b.PREFERENCE LIKE '%1A%'
AND b.PREFERENCE LIKE '%2A%'
AND b.PREFERENCE LIKE '%3A%'
AND b.PREFERENCE LIKE '%4A%'
AND b.PREFERENCE LIKE '%5A%'
AND b.PREFERENCE LIKE '%6A%'
AND b.PREFERENCE LIKE '%7A%'
AND b.PREFERENCE LIKE '%8A%'
AND b.PREFERENCE LIKE '%9A%'
AND b.NAME LIKE CONCAT('%', #{category_text}, '%')
AND REPLACE(b.TEL, '-', '') LIKE CONCAT('%', #{category_text}, '%')
AND (b.NAME LIKE CONCAT('%', #{category_text}, '%') OR REPLACE(b.TEL, '-', '') LIKE CONCAT('%', #{category_text}, '%'))
ORDER BY IDX DESC
) a WHERE no BETWEEN #{start} AND #{end}

/* get_real */
SELECT    IDX, NAME, TYPE, TEL, EMAIL, COUNTRY,
          CITY, PLACE_TYPE, PREFERENCE
FROM      REAL_ESTATE
WHERE     MEMBER_IDX = #{member_idx}
AND       IDX        = #{idx}
