mapper.select:
/* get_administrative_district */ SELECT IDX, CITY, DISTRICT FROM ADMINISTRATIVE_DISTRICT WHERE 1 = 1
AND COUNTRY = #{country}
AND CITY = (SELECT CITY FROM ADMINISTRATIVE_DISTRICT WHERE IDX = #{city}) AND DISTRICT != '전체'
AND DISTRICT = '전체'
ORDER BY CITY DESC, DISTRICT DESC

SELECT    COUNT(*)
FROM      MEMBER
WHERE     MATCH_KEY = #{MATCH_KEY}
